import { createContext, ReactNode, useEffect, useState } from "react";

type User = {
    id: string;
    nome: string;
    sobrenome: string;
    email: string;
    senha: string;
    dataNascimento : string;
    salario: number | null;
    diaSalario: number | null;

  }
  type AuthContextType = {
    user: User | undefined;
    signIn: (user: User) => void
  }

  type AuthContextProviderProps = {
      children: ReactNode
  }

export const AuthContext = createContext({} as AuthContextType);

export function AuthContextProvider (props: AuthContextProviderProps){
    const [user, setUser] = useState<User>();


  async function signIn(userInfo:User){
    const {id, nome, sobrenome, email, senha, dataNascimento, salario, diaSalario} = userInfo
    setUser({
      id : userInfo.id,
      nome: userInfo.nome,
      sobrenome:userInfo.sobrenome,
      email : userInfo.email,
      senha : userInfo.senha,
      dataNascimento : userInfo.dataNascimento,
      salario : userInfo.salario,
      diaSalario : userInfo.diaSalario,
    })

    console.log(user)

  }
 
 
    return (
        <AuthContext.Provider value ={{user, signIn}}>
            {props.children}
        </AuthContext.Provider>
    );
}
